function select(){const e=document.querySelectorAll(".select__header"),t=document.querySelectorAll(".select__item");e.forEach((e=>{e.addEventListener("click",(function(){this.parentElement.classList.toggle("select--active")}))})),t.forEach((e=>{e.addEventListener("click",(function(){let e=this.innerText,t=this.closest(".select");t.querySelector(".select__current").innerText=e,t.classList.remove("select--active")}))})),window.addEventListener("click",(function(e){const t=document.querySelectorAll(".select");e.target.closest(".select")||t.forEach((e=>{e.classList.remove("select--active")}))}))}select();const menu=document.querySelector(".menu"),hamburger=document.querySelector(".header__hamburger");hamburger.addEventListener("click",(function(){menu.classList.toggle("menu--open"),hamburger.classList.toggle("header__hamburger--menu-open")}));