function select(){let e=document.querySelectorAll(".select__header"),t=document.querySelectorAll(".select__item");e.forEach((e=>{e.addEventListener("click",(function(){this.parentElement.classList.toggle("select--active")}))})),t.forEach((e=>{e.addEventListener("click",(function(){let e=this.innerText,t=this.closest(".select");t.querySelector(".select__current").innerText=e,t.classList.remove("select--active")}))})),window.addEventListener("click",(function(e){let t=document.querySelectorAll(".select");e.target.closest(".select")||t.forEach((e=>{e.classList.remove("select--active")}))}))}select();